const productLists = [{
    productId : 1,
    productDesc : "Lorem, ipsum dolor sit amet consectetur adipisicing elit Tenetur dolor saepe, blanditiis modi perferendis reiciendis Nostrum quasi suscipit vitae ducimus harum culpa et distinctio sequi voluptas maiores", 
    productPrice : 20,
  },
  {
    productId : 2,
    productDesc : "Casual",
    productPrice : 21,

  },
  {
    productId : 3,
    productDesc : "Jeans",
    productPrice : 22,

  }]
const initialState = {
    counter: 0,
    products: productLists,
    product:[
      productId = 0,
      productDesc = "",
      productPrice = "",

    ]
  }

const rootReducer = (state = initialState, action) => {
    if (action.type === 'ADD') 
    {
      return {
        ...state,
        counter: state.counter + 1
      }
    }
    if (action.type === 'MIN') 
    {
      return {
        ...state,
        counter: state.counter - 1
      }
    }
    return state;
}

export default rootReducer;
