import productLists from "../pages/data"

const initialState = {
    products: productLists,
    product:[{
        productId : 0,
        productDesc : "",
        productPrice : "",
        productName : "",
        productMaterial : "",
    }],
    basket:[],
    total : 0,
  }

const productReducer = (state = initialState, action) => {
    if (action.type === 'ADD_ITEM') 
    {
      let addedItem = state.products.find(item=> item.productIid === action.productId)
      let existed_item= state.basket.find(item=> action.productIid === item.productIid)
      console.log("tes add item", existed_item)

      if(existed_item)
         {
            addedItem.quantity += 1 
             return{
                ...state,
                 total: state.total + addedItem.productPrice 
                  }
        }
         else{
            addedItem.quantity = 1;
            //calculating the total
            let newTotal = state.total + addedItem.productPrice 

            return{
                ...state,
                basket: [...state.basket, addedItem],
                total : newTotal
            }

        }




      // return {
      //     ...state,
      //     basket: [ ...state.basket, action.product
      //     ]
      // }
    }
    if (action.type === 'DEL_ITEM') 
    {
      const filteredProduct = state.basket
      .filter(
        product => String(product.productId) !== String(action.products.productId)
      )
      return {
          ...state,
          basket: filteredProduct
      }
    }
    return state;
}

export default productReducer;
