const initialState = {
    counter: 0
  }

const rootReducer = (state = initialState, action) => {
    if (action.type === 'ADD') 
    {
      return {
        ...state,
        counter: state.counter + 1
      }
    }

    if (action.type === 'SLIDE') 
    {
      if (state.counter === 3)
      {
        return {
          ...state,
          counter: state.counter - 3
        }
      }else{
        return {
          ...state,
          counter: state.counter + 1
        }
      }
    }
    if (action.type === 'MIN') 
    {
      return {
        ...state,
        counter: state.counter - 1
      }
    }
    return state;
}

export default rootReducer;
