import productLists from "../pages/data"

const initialState = {
    products: productLists,
    product:[{
        productId : 0,
        productDesc : "",
        productPrice : "",
        productName : "",
        productMaterial : "",
    }],
    basket:[],
    total : 0,
  }

const productReducer = (state = initialState, action) => {
    if (action.type === 'ADD_ITEM') 
    {
      let allItems = JSON.parse(localStorage.getItem('items'))
      console.log(state.total, "allitemss")
      //console.log("action", action.product.productId)
      let addedItem = state.products.find(item=> item.productId === action.product.productId)
      let existed_item= allItems.find(item => item.productId === action.product.productId) 

      if(existed_item)
         {
           console.log("ada")
            addedItem.quantity += 1 
             return{
                ...state,
                 total: state.total + addedItem.productPrice 
                  }
        }
         else{
           console.log("g ada item")
            addedItem.quantity = 1;
            //calculating the total
            let newTotal = state.total + addedItem.productPrice 

            return{
                ...state,
                basket: [...state.basket, addedItem],
                total : newTotal
            }

        }
      // return {
      //     ...state,
      //     basket: [ ...state.basket, action.product
      //     ]
      // }
    }
    if (action.type === 'DEL_ITEM') 
    {
      console.log("del" ,action)
      let allItems = JSON.parse(localStorage.getItem('items'))
      let itemToRemove= allItems.find(item=> action.product.productId === item.productId)
      let newItems = allItems.filter(item=> action.product.productId !== item.productId)
      console.log("ddd", allItems)
      let newTotal = state.total - (itemToRemove.productPrice * itemToRemove.quantity )
      return{
        ...state,
        basket: newItems,
        total: newTotal
    }
    }
    return state;
}

export default productReducer;
