import productLists from "../pages/data"

const initialState = {
    products: productLists,
    product:[{
        productId : 0,
        productDesc : "",
        productPrice : "",
    }],
    basket:[],
  }

const productReducer = (state = initialState, action) => {
    if (action.type === 'ADD_ITEM') 
    {
      return {
          ...state,
          basket: [ ...state.basket, action.product
          ]
      }
    }
    if (action.type === 'DEL_ITEM') 
    {
      const filteredProduct = state.basket
      .filter(
        todo => String(product.productId) !== String(action.products.productId)
      )
      return {
          ...state,
          basket: filteredProduct
      }
    }
    return state;
}

export default productReducer;
